        line.transformBy(m)
        pw.setColor(0xff0000)
        pw.drawMcDbEntity(line)

        pw.drawText("Translated line segment", MxFun.viewCoordLong2Cad(20), 0, line.endPoint.toVector3())

        const m1 = new McGeMatrix3d()
