        pw.drawMcDbEntity(line)
        pw.drawText("Scale the line segment by 0.5 times", MxFun.viewCoordLong2Cad(20), 0, new McGePoint3d((line.startPoint.x + line.endPoint.x) / 2, (line.startPoint.y + line.endPoint.y) / 2).toVector3())
        
